(117, 4, '2019-10-26 23:45:49', '2019-10-26 22:45:49', 'Test\ntest\nets\n<canvas id=\"myChart\" width=\"400\" height=\"400\"></canvas>\n<script><br />\nvar ctx = document.getElementById(\'myChart\').getContext(\'2d\');<br />\nvar myChart = new Chart(ctx, {<br />\n    type: \'bar\',<br />\n    data: {<br />\n        labels: [\'Red\', \'Blue\', \'Yellow\', \'Green\', \'Purple\', \'Orange\'],<br />\n        datasets: [{<br />\n            label: \'# of Votes\',<br />\n            data: [12, 19, 3, 5, 2, 3],<br />\n            backgroundColor: [<br />\n                \'rgba(255, 99, 132, 0.2)\',<br />\n                \'rgba(54, 162, 235, 0.2)\',<br />\n                \'rgba(255, 206, 86, 0.2)\',<br />\n                \'rgba(75, 192, 192, 0.2)\',<br />\n                \'rgba(153, 102, 255, 0.2)\',<br />\n                \'rgba(255, 159, 64, 0.2)\'<br />\n            ],<br />\n            borderColor: [<br />\n                \'rgba(255, 99, 132, 1)\',<br />\n                \'rgba(54, 162, 235, 1)\',<br />\n                \'rgba(255, 206, 86, 1)\',<br />\n                \'rgba(75, 192, 192, 1)\',<br />\n                \'rgba(153, 102, 255, 1)\',<br />\n                \'rgba(255, 159, 64, 1)\'<br />\n            ],<br />\n            borderWidth: 1<br />\n        }]<br />\n    },<br />\n    options: {<br />\n        scales: {<br />\n            yAxes: [{<br />\n                ticks: {<br />\n                    beginAtZero: true<br />\n                }<br />\n            }]<br />\n        }<br />\n    }<br />\n});<br />\n</script>', 'Welcome', '', 'inherit', 'closed', 'closed', '', '104-revision-v1', '', '', '2019-10-26 23:45:49', '2019-10-26 22:45:49', '', 104, 'https://www.climactiv.de/2019/10/26/104-revision-v1/', 0, 'revision', '', 0),
(118, 4, '2019-10-26 23:48:09', '2019-10-26 22:48:09', 'Test\ntest\nets\n<div style=\"width: 75%;\">\n<div class=\"chartjs-size-monitor\">\n<div class=\"chartjs-size-monitor-expand\">\n<div class=\"\"></div>\n</div>\n<div class=\"chartjs-size-monitor-shrink\">\n<div class=\"\"></div>\n</div>\n</div>\n<canvas id=\"canvas\" style=\"display: block; height: 624px; width: 1248px;\" width=\"2496\" height=\"1248\" class=\"chartjs-render-monitor\"></canvas>\n		<progress id=\"animationProgress\" max=\"1\" value=\"0\" style=\"width: 100%\"></progress>\n\n</div>\n<script><br />\n		var progress = document.getElementById(\'animationProgress\');<br />\n		var config = {<br />\n			type: \'line\',<br />\n			data: {<br />\n				labels: [\'January\', \'February\', \'March\', \'April\', \'May\', \'June\', \'July\'],<br />\n				datasets: [{<br />\n					label: \'My First dataset\',<br />\n					fill: false,<br />\n					borderColor: window.chartColors.red,<br />\n					backgroundColor: window.chartColors.red,<br />\n					data: [<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor()<br />\n					]<br />\n				}, {<br />\n					label: \'My Second dataset \',<br />\n					fill: false,<br />\n					borderColor: window.chartColors.blue,<br />\n					backgroundColor: window.chartColors.blue,<br />\n					data: [<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor(),<br />\n						randomScalingFactor()<br />\n					]<br />\n				}]<br />\n			},<br />\n			options: {<br />\n				title: {<br />\n					display: true,<br />\n					text: \'Chart.js Line Chart - Animation Progress Bar\'<br />\n				},<br />\n				animation: {<br />\n					duration: 2000,<br />\n					onProgress: function(animation) {<br />\n						progress.value = animation.currentStep / animation.numSteps;<br />\n					},<br />\n					onComplete: function() {<br />\n						window.setTimeout(function() {<br />\n							progress.value = 0;<br />\n						}, 2000);<br />\n					}<br />\n				}<br />\n			}<br />\n		};</p>\n<p>		window.onload = function() {<br />\n			var ctx = document.getElementById(\'canvas\').getContext(\'2d\');<br />\n			window.myLine = new Chart(ctx, config);<br />\n		};</p>\n<p>		document.getElementById(\'randomizeData\').addEventListener(\'click\', function() {<br />\n			config.data.datasets.forEach(function(dataset) {<br />\n				dataset.data = dataset.data.map(function() {<br />\n					return randomScalingFactor();<br />\n				});<br />\n			});</p>\n<p>			window.myLine.update();<br />\n		});<br />\n	</script>', 'Welcome', '', 'inherit', 'closed', 'closed', '', '104-revision-v1', '', '', '2019-10-26 23:48:09', '2019-10-26 22:48:09', '', 104, 'https://www.climactiv.de/2019/10/26/104-revision-v1/', 0, 'revision', '', 0),
