(1, 'dbconnect', '<?php\r\nfunction fastconnect() {\r\n\r\n$host = \"climactiv.de.mysql\";\r\n$user = \"climactiv_de\";\r\n$pwd = \"EkTRtrr46RxUfEQCzVAiMg8T\";\r\n$db = \"climactiv_de\";\r\n\r\n$link = mysqli_connect($host, $user, $pwd, $db);\r\n\r\nif (!$link) {\r\n    echo \"Error: Unable to connect to MySQL.\" . PHP_EOL;\r\n    echo \"Debugging errno: \" . mysqli_connect_errno() . PHP_EOL;\r\n    echo \"Debugging error: \" . mysqli_connect_error() . PHP_EOL;\r\n    exit;\r\n}\r\n\r\n// echo \"Success: A proper connection to MySQL was made! (\" . mysqli_get_host_info($link) . \")\" . PHP_EOL;\r\n\r\nreturn $link;\r\n\r\n}', '[xyz-ips snippet=\"dbconnect\"]', 1),
(2, 'leaderboard', '<?php\r\n\r\n$con = fastconnect();\r\n\r\n$query = \"SELECT ID, display_name, points FROM www_users ORDER BY points DESC\";\r\n$result = mysqli_query($con, $query);\r\n\r\necho \"<br>\";\r\necho \"<div class=\'ld\'>\";\r\n$i = 1;\r\nwhile ($row = mysqli_fetch_assoc($result)) { // Important line !!! Check summary get row on array ..\r\n\r\n        if ($i <= 9) {\r\n        $width = (70/280*$row[\'points\'])*6;\r\n        echo \"<div style=\'width:\" . $width . \"%\' class=\'ld-row\'>\";\r\n        echo \"<div class=\'ld-cell ld-number\'>\". $i . \"</div>\";\r\n        echo \"<div class=\'ld-cell ld-pic\' style=\'background-image: url(&#39;https://randomuser.me/api/portraits/women/\" . $row[\'ID\']  .\".jpg&#39;)\'></div>\";\r\n        echo \"<div class=\'ld-cell ld-colorbg\'>\";\r\n        echo \"<div class=\'ld-cell-inner ld-name\'>\" . $row[\'display_name\'] . \"</div>\"; \r\n        echo \"<div class=\'ld-cell-inner ld-spacer\' style=\'width:\" . $width . \"px\'></div>\"; \r\n        echo \"<div class=\'ld-cell-inner ld-points\' style=\'float: right\'>\" . $row[\'points\'] . \" pt</div>\"; \r\n        echo \"</div>\";\r\n        echo \"</div>\";\r\n        echo \"<br/>\";\r\n        $i++;\r\n}\r\n\r\n}\r\necho \"</div>\";', '[xyz-ips snippet=\"leaderboard\"]', 1),
(3, 'leaderboardSmall', '<?php\r\n\r\n$con = fastconnect();\r\n\r\n$query = \"SELECT ID, display_name, points FROM www_users ORDER BY points DESC\";\r\n$result = mysqli_query($con, $query);\r\n\r\necho \"<br>\";\r\necho \"<div class=\'ld\'>\";\r\n$i = 1;\r\nwhile ($row = mysqli_fetch_assoc($result)) { // Important line !!! Check summary get row on array ..\r\n\r\n        if ($i <= 3) {\r\n        $width = (1/600*$row[\'points\'])*6;\r\n        echo \"<div style=\'width:\" . $width . \"%\' class=\'ld-row\'>\";\r\n        echo \"<div class=\'ld-cell ld-number\'>\". $i . \"</div>\";\r\n        echo \"<div class=\'ld-cell ld-pic\' style=\'background-image: url(&#39;https://randomuser.me/api/portraits/women/\" . $row[\'ID\']  .\".jpg&#39;)\'></div>\";\r\n        echo \"<div class=\'ld-cell ld-colorbg\'>\";\r\n        echo \"<div class=\'ld-cell-inner\' style=\'width: 150px\'>\" . $row[\'display_name\'] . \"</div>\"; \r\n        echo \"<div class=\'ld-cell-inner ld-spacer\' style=\'width:\" . $width . \"px\'></div>\"; \r\n        echo \"<div class=\'ld-cell-inner ld-points\' style=\'float: right\'>\" . $row[\'points\'] . \" pt</div>\"; \r\n        echo \"</div>\";\r\n        echo \"</div>\";\r\n        echo \"<br/>\";\r\n        $i++;\r\n}\r\n\r\n}\r\necho \"</div>\";', '[xyz-ips snippet=\"leaderboardSmall\"]', 1),
